mapper.select:
SELECT id, memberName, memberBirth, memberid, memberEmail, memberDepartment, memberPosition, applicationDate, memberEmploymentDate
FROM member
WHERE id = #{id}

SELECT * FROM member


        memberName LIKE CONCAT('%', #{memberName}, '%')


        AND DATE(applicationDate) >= #{searchStartDate}


        AND DATE(applicationDate) <= #{searchEndDate}


ORDER BY applicationDate DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) FROM member


        memberName LIKE CONCAT('%', #{memberName}, '%')


        AND DATE(applicationDate) >= #{searchStartDate}


        AND DATE(applicationDate) <= #{searchEndDate}

mapper.update:
UPDATE member
SET memberDepartment = #{staff.memberDepartment},
    memberPosition = #{staff.memberPosition},
    employmentStatus = #{staff.employmentStatus}
WHERE id = #{staff.id}

mapper.select:
SELECT * FROM member


        memberName LIKE CONCAT('%', #{memberName}, '%')


        AND DATE(applicationDate) >= #{searchStartDate}


        AND DATE(applicationDate) <= #{searchEndDate}


        AND permission = #{permission}


ORDER BY applicationDate DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) FROM member


        memberName LIKE CONCAT('%', #{memberName}, '%')


        AND DATE(applicationDate) >= #{searchStartDate}


        AND DATE(applicationDate) <= #{searchEndDate}


        AND permission = #{permission}

mapper.update:
UPDATE member
SET permission = #{staff.permission}
WHERE id = #{staff.id}
