mapper.select:
SELECT id, member_name, member_birth, member_id, member_email, member_department, member_position, application_date, member_employmentdate, member_permission, member_reason
FROM member
WHERE id = #{id}

SELECT * FROM member


        member_name LIKE CONCAT('%', #{member_name}, '%')


        AND DATE(application_date) >= #{search_startdate}


        AND DATE(application_date) <= #{search_endate}


ORDER BY application_date DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) FROM member


        member_name LIKE CONCAT('%', #{member_name}, '%')


        AND DATE(application_date) >= #{search_startdate}


        AND DATE(application_date) <= #{search_endate}

mapper.update:
UPDATE member
SET member_department = #{staff.member_department},
    member_position = #{staff.member_position},
    employment_status = #{staff.employment_status}
WHERE id = #{staff.id}

