mapper.select:
SELECT * FROM member


        memberName LIKE CONCAT('%', #{memberName}, '%')


        AND DATE(applicationDate) >= #{searchStartDate}


        AND DATE(applicationDate) <= #{searchEndDate}


ORDER BY applicationDate DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) FROM member


        memberName LIKE CONCAT('%', #{memberName}, '%')


        AND DATE(applicationDate) >= #{searchStartDate}


        AND DATE(applicationDate) <= #{searchEndDate}

SELECT memberName, memberBirth, memberid, memberEmail, memberDepartment, memberPosition
FROM member
